-- Tạo database Blog
create
database blog;

-- Sử dụng database Blog
use
blog;

-- Tạo bảng User
create table user
(
    id       int         not null primary key auto_increment,
    name     varchar(60) not null,
    email    varchar(60) not null,
    password varchar(60) not null,
    image    text,
    sex      varchar(10),
    dob      varchar(30),
    address  text
);

-- Tạo bảng thể loại
create table category
(
    id   int not null primary key auto_increment,
    name varchar(20)
);


-- Tạo bảng trạng thái
create table situation
(
    id   int not null primary key auto_increment,
    name varchar(20)
);

-- Tạo bảng bài viết
create table post
(
    id          int not null primary key auto_increment,
    content     text,
    image       text,
    time        varchar(30),
    idSituation int,
    foreign key (idSituation) references situation (id),
    idCategory  int,
    foreign key (idCategory) references category (id),
    idUser      int,
    foreign key (idUser) references user (id)
);

-- Tạo bảng bình luận
create table comment
(
    id      int not null primary key auto_increment,
    time    varchar(20),
    content text,
    idPost  int,
    foreign key (idPost) references post (id),
    idUser  int,
    foreign key (idUser) references user (id)
);

-- Tạo bảng tính năng kết bạn
create table friendship
(
    id      int not null primary key auto_increment,
    idUser1 int,
    foreign key (idUser1) references user (id),
    idUser2 int,
    foreign key (idUser2) references user (id)
)
